import React, {Component} from 'react';

class MonsterEyes extends Component{

    constructor(props){
        super(props)
        this.state = { left:0, top:0, rendered:false }

    }

    render(){
        if (!this.state.rendered){
            const left_coord = Math.round(Math.random() * this.props.width)
            const top_coord = Math.round(Math.random() * (this.props.height - 400))
            this.setState({ left:left_coord, top:top_coord, rendered:true })
        }
        return (
            <div className="monster" style={{left:this.state.left, top:this.state.top}} >
                <div className="monster_sprite"></div>
                <h3>{this.props.width}</h3>
            </div>
        )
    }

}

export default MonsterEyes
