import React, {Component} from 'react';

class MonsterEyes extends Component{

    constructor(props){
        super(props)
        this.state = { left:0, top:0, rendered:true }
    }

    render(){
        if (this.state.rendered){
            const left_coord = Math.round(Math.random() * this.props.width)
            const top_coord = Math.round(Math.random() * this.props.height)
            this.setState({ left:left_coord, top:top_coord, rendered:false })
        }
        return (
            <div className="monster" >
                <div className="monster_sprite"></div>
                <h3>{this.props.height} {this.props.width} {this.state.top} {this.state.left} {this.state.rendered}</h3>
            </div>
        )
    }

}

export default MonsterEyes
