import React, {Component} from 'react';

class MonsterEyes extends Component{

    constructor(props){
        super(props)
        this.state = {number: 0, top:0, left:0}
    }

    addMonsters(){
        // if (this.state.number < 20){
            const left = Math.round(Math.random() * this.props.width)
            const top = Math.round(Math.random() * (this.props.height + 400))
            this.setState({left:left})
            this.setState({top:top})
        // }
    }
// setInterval(function(){ alert("Hello"); }, 3000);
    // setTimeout(()=>this.addMonsters(), 10000)
    render(){
        setInterval(()=>this.addMonsters(), 10000);


        return (
            <div className="monster" style={{left:this.state.left, top:this.state.top}} >
                <div className="monster_sprite"></div>
                <h3>{this.props.width} {this.state.number}</h3>
            </div>
        )
    }

}

export default MonsterEyes
