import React, {Component} from 'react';
import data from './monsterEyesLocations'

class MonsterEyes extends Component{

    constructor(props){
        super(props)
        this.state = { index: Math.floor(props.number*Math.random())}
        this.width = this.props.width
        this.height = this.props.height
        this.eyes = data.map( monster =>
            <div className="monster"  style={{left:(monster.left-this.width), top:monster.top}} >
                <div className="monster_sprite"></div>
            </div>
        )
    }

    render(){

        return (
            <div className="monsterEyesContainer">
                {this.eyes}
            </div>
            )
    }

}

export default MonsterEyes


