import React, {Component} from 'react';

class MonsterEyes extends Component{

    constructor(props){
        super(props)
        this.state = {number: 0}
    }

    addMonsters(){
        if (this.state.number < 20){
            const left = Math.round(Math.random() * this.props.width)
            const top = Math.round(Math.random() * this.props.height)
            this.setState({number:left})
        }
    }

    render(){
        setTimeout(()=>this.addMonsters(), 1000)

        return (
            <div className="monster" style={{left:0, top:this.props.height-500}} >
                <div className="monster_sprite"></div>
                <h3>{this.props.width} {this.state.number}</h3>
            </div>
        )
    }

}

export default MonsterEyes
